// File: apps/election-mvp/server/domain/Bundle.ts
class BundleAggregate {
  constructor(
    private id: string,
    private products: BundleProduct[],
    private budgetRange: BudgetRange,
    private targetAudience: TargetAudience
  ) {}

  removeProduct(productId: string): DomainResult<void> {
    // Validation AVANT modification
    const productsAfterRemoval = this.products.filter(p => p.id !== productId)
    const validationResult = this.validateState({
      ...this,
      products: productsAfterRemoval
    })

    if (!validationResult.isValid) {
      return DomainResult.failure(validationResult.errors)
    }

    // Modification seulement si validation OK
    this.products = productsAfterRemoval
    this.addDomainEvent(new ProductRemovedEvent(this.id, productId))

    return DomainResult.success()
  }

  private validateState(bundle: Bundle): ValidationResult {
    const errors = []

    // Business rules encapsulated
    if (!this.budgetRange.isValidForTotal(bundle.total)) {
      errors.push(new BudgetRangeViolation(bundle.total, this.budgetRange))
    }

    if (!this.targetAudience.isValidForQuantity(bundle.totalQuantity)) {
      errors.push(new AudienceQuantityViolation(bundle.totalQuantity, this.targetAudience))
    }

    return { isValid: errors.length === 0, errors }
  }
}
```

