// File: apps/election-mvp/composables/useBundleHistory.ts
interface BundleHistoryState {
  state: Bundle
  action: string
  timestamp: Date
  actionData?: any
}

const useBundleHistory = () => {
  const history = ref<BundleHistoryState[]>([])
  const currentIndex = ref(0)

  const pushState = (state: Bundle, action: string, data?: any) => {
    // Truncate future history if we're not at the end
    history.value = history.value.slice(0, currentIndex.value + 1)

    history.value.push({
      state: cloneDeep(state),
      action,
      timestamp: new Date(),
      actionData: data
    })

    currentIndex.value = history.value.length - 1
  }

  const undo = () => {
    if (currentIndex.value > 0) {
      currentIndex.value--
      return history.value[currentIndex.value].state
    }
  }

  const redo = () => {
    if (currentIndex.value < history.value.length - 1) {
      currentIndex.value++
      return history.value[currentIndex.value].state
    }
  }

  return { pushState, undo, redo, canUndo, canRedo }
}
```

